import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { Button } from './Button';

function Navbar() {
    const [click, setClick] = useState(false);
    const handleClick = () => setClick(!click);
    const closeMobielMenu = () => setClick(false);
    const [button, setButton]=useState(true);
    const showButton=()=>{
        if(window.innerWidth <=960){
            setButton(false);
        }
    }


    return (
        <div>
            <nav className="navbar">
                <div className="nav-container">
                    <Link to="/" className="navbar-logo">TRVL <i className="fab fa-typo3" />
                    </Link>
                    <div className="menu-icon" onClick={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                    </div>
                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                        <li className='nav-item'></li>
                        <Link to='/home' className='nav-links' onClick={closeMobielMenu}>Home</Link>
                        <li className='nav-item'></li>
                        <Link to='/services' className='nav-links' onClick={closeMobielMenu}>Services</Link>
                        <li className='nav-item'></li>
                        <Link to='/products' className='nav-links' onClick={closeMobielMenu}>Products</Link>
                        <li className='nav-item'></li>
                        <Link to='/signup' className='nav-links-mobile' onClick={closeMobielMenu}>sign up</Link>
                    </ul>
                    {button && <Button buttonStyle='btn--outline'>SIGN UP</Button>}
                </div>
            </nav>
        </div>
    )
}

export default Navbar;
